        public static function updatePositionShop($id_shop)
        {
            Db::getInstance()->execute('
                CREATE TABLE IF NOT EXISTS `'._DB_PREFIX_.'appagebuilder_positions_shop` (
                  `id_appagebuilder_positions` int(11) NOT NULL AUTO_INCREMENT,
                  `id_shop` int(10) unsigned NOT NULL,
                  PRIMARY KEY (`id_appagebuilder_positions`, `id_shop`)
                ) ENGINE='._MYSQL_ENGINE_.' DEFAULT CHARSET=UTF8;
            ');
            //$proFiles = Db::getInstance()->executes(
            $proFiles = Db::getInstance()->executes('SELECT id_appagebuilder_profiles as id, id_shop FROM `'._DB_PREFIX_.'appagebuilder_profiles_shop`');
            //echo '<pre>';print_r($proFiles);die;
            foreach ($proFiles as $pro) {
                $proFileModel = new ApPageBuilderProfilesModel($pro['id']);

                $sql = 'INSERT INTO `'._DB_PREFIX_.'appagebuilder_positions_shop` (`id_shop`, `id_appagebuilder_positions`)
				VALUES('.(int)$pro['id_shop'].', '.(int)$proFileModel->header.')';
                Db::getInstance()->execute($sql);
                //echo $sql.'<br/>';
                $sql = 'INSERT INTO `'._DB_PREFIX_.'appagebuilder_positions_shop` (`id_shop`, `id_appagebuilder_positions`)
				VALUES('.(int)$pro['id_shop'].', '.(int)$proFileModel->content.')';
                Db::getInstance()->execute($sql);
                //echo $sql.'<br/>';
                $sql = 'INSERT INTO `'._DB_PREFIX_.'appagebuilder_positions_shop` (`id_shop`, `id_appagebuilder_positions`)
				VALUES('.(int)$pro['id_shop'].', '.(int)$proFileModel->footer.')';
                //Db::getInstance()->execute($sql);
                //echo $sql.'<br/>';
                $sql = 'INSERT INTO `'._DB_PREFIX_.'appagebuilder_positions_shop` (`id_shop`, `id_appagebuilder_positions`)
				VALUES('.(int)$pro['id_shop'].', '.(int)$proFileModel->product.')';
                Db::getInstance()->execute($sql);
            }
        }

        public static function installModuleTab()
        {
            $module = new APPageBuilder();
            //create parent tab
            $newtab = new Tab();
            $newtab->class_name = 'AdminApPageBuilder';
            $newtab->id_parent = 0;
            $newtab->module = 'appagebuilder';
            foreach (Language::getLanguages() as $l) {
                $newtab->name[$l['id_lang']] = $module->l('Ap PageBuilder');
            }
            if ($newtab->save()) {

                $id_parent = $newtab->id;

                foreach (self::getTabs() as $tab) {
                    $newtab = new Tab();
                    $newtab->class_name = $tab['class_name'];
                    $newtab->id_parent = isset($tab['id_parent']) ? $tab['id_parent'] : $id_parent;
                    $newtab->module = 'appagebuilder';
                    foreach (Language::getLanguages() as $l) {
                        $newtab->name[$l['id_lang']] = $module->l($tab['name']);
                    }
                    $newtab->save();
                }
            }
        }

        public static function uninstall()
        {
            self::uninstallModuleTab();
            return Db::getInstance()->execute('
                DROP TABLE IF EXISTS `'._DB_PREFIX_.'appagebuilder_profiles`, `'._DB_PREFIX_.'appagebuilder_profiles_shop`, `'.
                            _DB_PREFIX_.'appagebuilder_products`, `'._DB_PREFIX_.'appagebuilder_products_shop` , `'.
                            _DB_PREFIX_.'appagebuilder`, `'._DB_PREFIX_.'appagebuilder_shop`, `'._DB_PREFIX_.'appagebuilder_lang`, `'._DB_PREFIX_.'appagebuilder_positions`, `'._DB_PREFIX_.'appagebuilder_positions_shop`;
            ');
        }

        public static function uninstallModuleTab()
        {
            $id = Tab::getIdFromClassName('AdminApPageBuilder');
            if ($id) {
                $tab = new Tab($id);
                $tab->delete();
            }

            foreach (self::getTabs() as $tab) {
                $id = Tab::getIdFromClassName($tab['class_name']);
                if ($id) {
                    $tab = new Tab($id);
                    $tab->delete();
                }
            }
        }
    }

